		UPDATE itc_investigations_audit.itc_complainant_aud
		SET user_name = 'itc_etl_app'
		where _id= i_source_pk_val and action = 'U' and __complainant_inactive_date;

	END IF;
	
	
END IF;

	EXCEPTION
	    WHEN OTHERS THEN
	        RAISE;
END;
$$;


