          EXCEPT SELECT lit_id, case_key FROM university
          EXCEPT SELECT lit_id, case_key FROM inventor
          EXCEPT SELECT lit_id, case_key FROM oc
          EXCEPT SELECT lit_id, case_key FROM gov
        ) T
				INNER JOIN core.lit_types lt ON (
					/*lt. NAME = ''undefined''*/
					lt. ID = 21
