    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
declare
  v_sql varchar := '';
  v_col_name text;
  v_count integer;
begin

raise notice 'DELETE: ';
for v_col_name in
  select column_name
  from information_schema.columns
  where table_name = p_table_name and table_schema = 'core_audit'
  and substr(column_name, 1, 2) = '__'
loop
  v_sql := '
   select count(*)
   from core_audit.' || p_table_name || '
   where created_at::DATE = ''' || p_date_changed || '''::DATE
   and ' || v_col_name || '= TRUE
   and action = ''D'';';
  execute v_sql into v_count;
  --raise notice 'Column: %', v_col_name;
  --raise notice 'SQL: %', v_sql;
  if (v_count > 0) then
    raise notice 'Column % updated % times.', v_col_name, v_count;
  end if;
end loop;

raise notice 'INSERT: ';
for v_col_name in
  select column_name
  from information_schema.columns
  where table_name = p_table_name and table_schema = 'core_audit'
  and substr(column_name, 1, 2) = '__'
loop
  v_sql := '
   select count(*)
   from core_audit.' || p_table_name || '
   where created_at::DATE = ''' || p_date_changed || '''::DATE
   and ' || v_col_name || '= TRUE
   and action = ''I'';';
  execute v_sql into v_count;
  --raise notice 'Column: %', v_col_name;
  --raise notice 'SQL: %', v_sql;
  if (v_count > 0) then
    raise notice 'Column % updated % times.', v_col_name, v_count;
  end if;
end loop;

raise notice 'UPDATE: ';
for v_col_name in
  select column_name
  from information_schema.columns
  where table_name = p_table_name and table_schema = 'core_audit'
  and substr(column_name, 1, 2) = '__'
loop
  v_sql := '
   select count(*)
   from core_audit.' || p_table_name || '
   where created_at::DATE = ''' || p_date_changed || '''::DATE
   and ' || v_col_name || '= TRUE
   and action = ''U'';';
  execute v_sql into v_count;
  --raise notice 'Column: %', v_col_name;
  --raise notice 'SQL: %', v_sql;
  if (v_count > 0) then
    raise notice 'Column % updated % times.', v_col_name, v_count;
  end if;
end loop;

return 'Done.';
end;
$$;


