CREATE VIEW v_docdb_priority_claims AS
 SELECT docdb_priority_claims.id,
    docdb_priority_claims.pat_id,
    docdb_priority_claims.sequence,
    docdb_priority_claims.doc_number,
    docdb_priority_claims.updated_at,
