           FROM docs_materialized.ptab_institution_decision_party_info ind
          WHERE (((ind.status)::text = 'Supervisor Accepted'::text) AND (ind.ptab_party_type = ANY (ARRAY['1'::text, '2'::text, '3'::text, '4'::text])))
